option.value:
window.addKeydownListener = (dotNetHelper) => {
    const handleKeydown = (event) => {
        // Only handle number keys (0-9) and ignore if any modifier keys are pressed
        if (event.key >= '0' && event.key <= '9' && !event.ctrlKey && !event.altKey && !event.metaKey) {
            // Prevent default behavior for number keys
            event.preventDefault();

            // Call the C# method
            dotNetHelper.invokeMethodAsync('HandleKeyPress', event.key);
        }
    };

    // Add event listener to document
    document.addEventListener('keydown', handleKeydown);

    // Store the handler so it can be removed later if needed
    window.currentKeydownHandler = handleKeydown;
};

window.removeKeydownListener = () => {
    if (window.currentKeydownHandler) {
        document.removeEventListener('keydown', window.currentKeydownHandler);
        window.currentKeydownHandler = null;
    }
};
